settings.custom_liquid:
{% if page.title == 'User Account Custom' %}
{% if customer %}
  <div style="padding: 20px; border: 1px solid #ccc; border-radius: 5px; max-width: 500px; margin: 0 auto; text-align: center;">
    <h2 style="font-size: 24px; margin-bottom: 10px;">Customer Information</h2>
    <p style="font-size: 16px; margin-bottom: 5px;">Name: {{ customer.first_name }} {{ customer.last_name }}</p>
    <p style="font-size: 16px; margin-bottom: 5px;">Email: {{ customer.email }}</p>
    <p style="font-size: 16px; margin-bottom: 5px;">Phone: {{ customer.phone }}</p>
    <p style="font-size: 16px; margin-bottom: 5px; font-style: italic;">Default Address:
      {{ customer.default_address.address1 }},
      {{ customer.default_address.city }},
      {{ customer.default_address.province }},
      {{ customer.default_address.country }}
    </p>
  </div>
{% else %}
  <div style="padding: 20px; border: 1px solid #ccc; border-radius: 5px; max-width: 500px; margin: 0 auto; text-align: center;">
    <p style="font-size: 16px; font-style: italic;">No customer information available.</p>
  </div>

{% endif %}

{% endif %}


{% if page.title == 'Product Page Custom' %}
<div class="product-grid">
  {% for product in collections.all.products %}
    <div class="product-item">
      <img src="{{ product.featured_image.src | img_url: 'medium' }}" alt="{{ product.title }}">
      <h2>{{ product.title }}</h2>
      <p>{{ product.price | money }}</p>
      <button class="blue-button btn btn-outline-primary"><a href="{{ product.url }}" class="btn">View the product</a></button>
    </div>
  {% endfor %}
</div>
{% endif %}
